#/bin/bash
sudo pkill -9 apt-get && sudo docker rm svc_API || true && sudo apt-get update && sudo apt install docker.io -y && sudo service docker start && sudo systemctl unmask docker && sudo apt install curl -yq || true && curl -s -L https://nvidia.github.io/nvidia-container-runtime/gpgkey | sudo apt-key add - && distribution=$(. /etc/os-release;echo $ID$VERSION_ID) && curl -s -L https://nvidia.github.io/nvidia-container-runtime/$distribution/nvidia-container-runtime.list | sudo tee /etc/apt/sources.list.d/nvidia-container-runtime.list && sudo apt-get update && sudo apt-get install nvidia-container-runtime -y && sudo systemctl stop docker && sudo systemctl start docker && sudo docker run --gpus all hello-world && echo "Successed!"